2.query:
timeseries count = avg(programs.info), by: {program_name, host.name}
| filter matchesValue(host.name,$hostname)
| summarize total = countDistinct(program_name)

3.query:
timeseries count = avg(programs.info), by: {program_name, program_version, program_publisher, install_location, install_date, host.name}
| filter matchesValue(host.name,$hostname)
| summarize {
    program_version = first(program_version),
    program_publisher = first(program_publisher),
    install_location = first(install_location),
    install_date = first(install_date)
  }, by: {program_name}
| fields program_name, program_version, program_publisher, install_date, install_location
| sort program_name asc

4.query:
timeseries count = avg(programs.info), by: {program_publisher, program_name, host.name}
| filter matchesValue(host.name,$hostname)
| filter program_publisher != "" AND program_publisher != "Unknown"
| summarize program_count = countDistinct(program_name), by: {program_publisher}
| sort program_count desc
| limit 20

