2.query:
timeseries count = avg(programs.info), by: {host.name}
| filter matchesValue(host.name,$hostname)
| summarize host_count = countDistinct(host.name)

4.query:
timeseries count = avg(programs.info), by: {program_name, program_version, program_publisher, install_location, install_date, host.name}
| filter matchesValue(host.name,$hostname)
| filter matchesValue(program_name,$software_name)
| summarize {
    program_version = first(program_version),
    program_publisher = first(program_publisher),
    install_location = first(install_location),
    install_date = first(install_date)
  }, by: {program_name, host.name}
| fields program_name, program_version, program_publisher, install_date, install_location, host.name
| sort program_name asc

6.query:
timeseries count = avg(programs.info), by: {program_name, host.name}
| filter matchesValue(host.name,$hostname)
| summarize install_count = countDistinct(host.name), by: {program_name}
| sort install_count desc
| limit 20
